import cookie from 'js-cookie';
import Router from 'next/router'; // set in cookie

export var setCookie = function setCookie(key, value) {
  if (true) {
    cookie.set(key, value, {
      expires: 1
    });
  }
}; // remove from cookie

export var removeCookie = function removeCookie(key) {
  if (true) {
    cookie.remove(key);
  }
}; // get from cookie such as stored token
// will be useful when we need to make request to server with auth token

export var getCookie = function getCookie(key, req) {
  // if (process.browser) {
  //     return cookie.get(key);
  // }
  return true ? getCookieFromBrowser(key) : getCookieFromServer(key, req);
};
export var getCookieFromBrowser = function getCookieFromBrowser(key) {
  return cookie.get(key);
};
export var getCookieFromServer = function getCookieFromServer(key, req) {
  if (!req.headers.cookie) {
    return undefined;
  } // console.log('req.headers.cookie', req.headers.cookie);


  var token = req.headers.cookie.split(';').find(function (c) {
    return c.trim().startsWith("".concat(key, "="));
  });

  if (!token) {
    return undefined;
  }

  var tokenValue = token.split('=')[1]; // console.log('getCookieFromServer', tokenValue);

  return tokenValue;
}; // set in localstoarge

export var setLocalStorage = function setLocalStorage(key, value) {
  if (true) {
    localStorage.setItem(key, JSON.stringify(value));
  }
}; // remove from localstorage

export var removeLocalStorage = function removeLocalStorage(key) {
  if (true) {
    localStorage.removeItem(key);
  }
}; // authenticate user by passing data to cookie and localstorage during signin

export var authenticate = function authenticate(response, next) {
  setCookie('token', response.data.token);
  setLocalStorage('user', response.data.user);
  next();
}; // access user info from localstorage

export var isAuth = function isAuth() {
  if (true) {
    var cookieChecked = getCookie('token');

    if (cookieChecked) {
      if (localStorage.getItem('user')) {
        return JSON.parse(localStorage.getItem('user'));
      } else {
        return false;
      }
    }
  }
};
export var logout = function logout() {
  removeCookie('token');
  removeLocalStorage('user');
  Router.push('/login');
};
